const { help, list, select, size } = require("./index.js");

const run = async (arguments) => {

    if(arguments[2] == "help" || arguments[2] == null || arguments[2] == "?") {
        await help();
    } else if(arguments[2] == "list" || arguments[2] == "ls") {
        await list();
    } else if(arguments[2] == "select" || arguments[2] == "choose" || arguments[2] == "use") {
        await select(arguments[3]);
    } else if(arguments[2] == "size" || arguments[2] == "largeness") {
        await size(arguments[3]);
    }

}

run(process.argv);