const chalk = require("chalk");
const orange = chalk.keyword("orange");

const help = chalk.keyword("orange")(`
___         
(_  '/_ _ _ _
/  /((-(/(-/ 
      _/     

`) + chalk.keyword("orange")("usage:") + ` fl <command> [<args>]

` + chalk.keyword("orange")("commands:") + `
fl help
fl list
fl select [path]
fl size [path]`

const select = {
    "success": (path) => `Successfully selected ` + chalk.keyword("orange")(path) + `!`,
    "failure": (path) => chalk.bold.red(`Failed to select ` + chalk.keyword("orange")(path + "!")),
    "noPath": (currentPath) => `Currently selected: ` + chalk.keyword("orange")(currentPath) + `

To select a file, run:
` + chalk.keyword("orange")("fl select [path]")
}
const size = {
    "success": (path, size) => `The size of ` + orange(path) + ` is ` + orange(size + "MB!"),
    "failure": (path) => "Failed to find size of " + orange(path + "!")
}

module.exports = {
    help,
    select,
    size
}