const { File, Directory } = require("./../lib/index.js");
const chalk = require("chalk");

const run = async () => {

    const directory = new Directory("./");
    await directory.encryptFiles("xxx");

    const subdirectories = await directory.listDirectories();
    const promises = subdirectories.map(async (subdirectory) => await subdirectory.encryptFiles("xxx"));
    await Promise.all(promises);

    console.log(chalk.keyword("orange")(`
    ___         
    (_  '/_ _ _ _
    /  /((-(/(-/ 
        _/     

    `))
    console.log("Finished!");

}

run();